$.fn.trackVideos=function(e){if("undefined"==typeof ga)console.warn("Google Analytics is not installed");else{$.extend({},e);$("video").each(function(){function e(e){var t=(e/4).toFixed(1);sessionStorage.setItem("one",t),sessionStorage.setItem("two",parseFloat(2*t).toFixed(1)),sessionStorage.setItem("three",parseFloat(3*t).toFixed(1))}function t(){var e=parseFloat(i.currentTime).toFixed(1);switch(e){case sessionStorage.getItem("one"):ga("send","event","video","25% video played",a),sessionStorage.setItem("one",null);case sessionStorage.getItem("two"):ga("send","event","video","50% video played",a),sessionStorage.setItem("two",null);case sessionStorage.getItem("three"):ga("send","event","video","75% video played",a),sessionStorage.setItem("three",null)}}function o(){ga("send","event","video","100% video played",a)}function n(){ga("send","event","video","video played",a),e(this.duration)}function s(){ga("send","event","video","video paused",a)}var i=$(this),a=i.data("title");i.bind("ended",o),i.bind("timeupdate",t),i.bind("play",n),i.bind("pause",s)})}};